import React from 'react';
import "./App.css";
import "./TripInfo/TripInfo.css"; 
import TripInfo from './TripInfo/TripInfo';
import InputForm from './TripInfo/InputForm';

class TripInfoControl extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      userisLoggedIn: false, 
      userHasProfile: false,
      formVisible: true, 
      selectedDestination: null,
      selectedTripFrom: null,
      selectedTripUntil: null, 
      selectedDestinations: null,
      homeLocation: null
    }
  }
  render(){
    return(
      <React.Fragment>
        <InputForm/>
        <TripInfo/>
      </React.Fragment>
    )
  }

};

export default TripInfoControl;
